// src/components/KanbanBoard.tsx

import React, { useState } from 'react';
import Task from './Task';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import Modal from 'react-modal';
import { Task as TaskType } from '../components/types';

Modal.setAppElement('#root'); // Set the app element for accessibility

const KanbanBoard: React.FC = () => {
  const [tasks, setTasks] = useState<TaskType[]>([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);

  const addTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!title || !description) return;
    const newTask: TaskType = {
      id: Date.now().toString(),
      title,
      description,
      status: 'TODO',
    };
    setTasks([...tasks, newTask]);
    setTitle('');
    setDescription('');
  };

  const openModal = (task: TaskType) => {
    setTitle(task.title);
    setDescription(task.description);
    setCurrentTaskId(task.id);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTaskId(null);
  };

  const handleEditTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentTaskId) return;

    const updatedTasks = tasks.map(task => 
      task.id === currentTaskId ? { ...task, title, description } : task
    );
    setTasks(updatedTasks);
    closeModal();
  };

  const onDragEnd = (result: any) => {
    if (!result.destination) return;

    const updatedTasks = Array.from(tasks);
    const [movedTask] = updatedTasks.splice(result.source.index, 1);

    // Set the new status based on the destination droppableId
    movedTask.status = result.destination.droppableId;
    updatedTasks.splice(result.destination.index, 0, movedTask);

    setTasks(updatedTasks);
  };

  return (
    <div>
      <h1>Kanban TODO App</h1>
      <form onSubmit={addTask}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">Add Task</button>
      </form>

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          {['TODO', 'DOING', 'DONE'].map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    width: '30%',
                    padding: '10px',
                    border: '1px solid black',
                    backgroundColor: status === 'TODO' ? 'lightyellow' : status === 'DOING' ? 'lightblue' : 'lightgreen',
                  }}
                >
                  <h2>{status}</h2>
                  {tasks
                    .filter(task => task.status === status)
                    .map((task, index) => (
                      <Task key={task.id} task={task} index={index} onClick={() => openModal(task)} />
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>

      {/* Modal for editing tasks */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Edit Task"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
          },
        }}
      >
        <h2>Edit Task</h2>
        <form onSubmit={handleEditTask}>
          <input
            type="text"
            placeholder="Title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
          />
          <textarea
            placeholder="Description"
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
          <button type="submit">Save Changes</button>
          <button type="button" onClick={closeModal}>Cancel</button>
        </form>
      </Modal>
    </div>
  );
};

export default KanbanBoard;

export {}
