// src/components/Task.tsx

import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { Task as TaskType } from '../types';
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import VisibilityIcon from "@mui/icons-material/Visibility";
import ClearIcon from "@mui/icons-material/Clear";

interface TaskProps {
  task: TaskType;
  index: number; // Position in the list
  onClick: () => void; // Handle click to open modal
  onDelete: () => void;
}

const Task: React.FC<TaskProps> = ({ task, index,onClick, onDelete }) => {
    const getColor = (status: string) => {
      switch (status) {
        case 'TODO':
          return 'yellow';
        case 'DOING':
          return 'blue';
        case 'DONE':
          return 'green';
        default:
          return 'white';
      }
    };

  return (
    <Draggable key={task.id} draggableId={task.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          // onClick={onClick} // Handle click to open modal
          style={{
            backgroundColor: getColor(task.status),
            border: '1px solid black',
            borderRadius: "10px",
            padding: '10px',
            margin: '5px',
            cursor: 'grab',
            ...provided.draggableProps.style,
          }}
        >
             <ClearIcon onClick={onDelete} />
          <h3>Titel : {task.title}</h3>
          <p>Description : {task.description}</p>

          <div className="card_footer">
            <p>
              <AccessTimeIcon />
              24th Oct
            </p>
            <p>
              <VisibilityIcon onClick={onClick}/>
            </p>
          </div>


        </div>
      )}
    </Draggable>
  );
};

export default Task;
