// src/components/Task.tsx

import React from 'react';
import { Draggable } from 'react-beautiful-dnd';
import { Task as TaskType } from '../types';

interface TaskProps {
  task: TaskType;
  index: number; // Position in the list
  onClick: () => void; // Handle click to open modal
}

const Task: React.FC<TaskProps> = ({ task, index, onClick }) => {
  const getColor = (status: string) => {
    switch (status) {
      case 'TODO':
        return 'lightyellow';
      case 'DOING':
        return 'lightblue';
      case 'DONE':
        return 'lightgreen';
      default:
        return 'white';
    }
  };

  return (
    <Draggable key={task.id} draggableId={task.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          onClick={onClick} // Handle click to open modal
          style={{
            backgroundColor: getColor(task.status),
            border: '1px solid black',
            padding: '10px',
            margin: '5px',
            cursor: 'grab',
            ...provided.draggableProps.style,
          }}
        >
          <h3>{task.title}</h3>
          <p>{task.description}</p>
        </div>
      )}
    </Draggable>
  );
};

export default Task;
