// src/components/KanbanBoard.tsx

import React, { useState } from 'react';
import Task from '../Task/Task';
import TaskForm from '../TaskForm/TaskForm';
import Modal from 'react-modal';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import { Task as TaskType } from '../types';

Modal.setAppElement('#root'); // Set the app element for accessibility

const KanbanBoard: React.FC = () => {
  const [tasks, setTasks] = useState<TaskType[]>([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [currentTask, setCurrentTask] = useState<TaskType | null>(null);

  const openModal = (task: TaskType) => {
    setCurrentTask(task);
    setCurrentTaskId(task.id);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTaskId(null);
    setCurrentTask(null);
  };

  const handleEditTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentTask) return;

    const updatedTasks = tasks.map(task =>
      task.id === currentTaskId ? { ...task, title: currentTask.title, description: currentTask.description } : task
    );
    setTasks(updatedTasks);
    closeModal();
  };

  const onDragEnd = (result: any) => {
    if (!result.destination) return; // If the task is dropped outside

    const updatedTasks = Array.from(tasks);
    const [movedTask] = updatedTasks.splice(result.source.index, 1); // Remove the task from its original position

    // Update the status of the moved task based on the droppableId
    movedTask.status = result.destination.droppableId as 'TODO' | 'DOING' | 'DONE'; // Ensure the type is correct

    // Insert the moved task into its new position
    updatedTasks.splice(result.destination.index, 0, movedTask);

    // Update state with the new tasks array
    setTasks(updatedTasks);
  };

  const addTask = (task: TaskType) => {
    setTasks([...tasks, task]); // Add the task directly
  };

  return (
    <div>
      <h1>Kanban TODO App</h1>

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: 'flex', justifyContent: 'space-between' }}>
          {(['TODO', 'DOING', 'DONE'] as const).map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    width: '30%',
                    padding: '10px',
                    border: '1px solid black',
                    backgroundColor: status === 'TODO' ? 'lightyellow' : status === 'DOING' ? 'lightblue' : 'lightgreen',
                  }}
                >
                  <h2>{status}</h2>

                  {/* TaskForm inside the column */}
                  <TaskForm addTask={addTask} status={status} />

                  {tasks
                    .filter(task => task.status === status)
                    .map((task, index) => (
                      <Task key={task.id} task={task} index={index} onClick={() => openModal(task)} />
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>

      {/* Modal for editing tasks */}
      <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Edit Task"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
          },
        }}
      >
        <h2>Edit Task</h2>
        {currentTask && (
          <form onSubmit={handleEditTask}>
            <input
              type="text"
              placeholder="Title"
              value={currentTask.title}
              onChange={(e) => setCurrentTask({ ...currentTask, title: e.target.value })}
              required
            />
            <textarea
              placeholder="Description"
              value={currentTask.description}
              onChange={(e) => setCurrentTask({ ...currentTask, description: e.target.value })}
              required
            />
            <button type="submit">Save Changes</button>
            <button type="button" onClick={closeModal}>Cancel</button>
          </form>
        )}
      </Modal>
    </div>
  );
};

export default KanbanBoard;
