// src/components/Task.tsx

import React from 'react';
import { Draggable } from 'react-draggable';
import { Task as TaskType } from '../types';

interface TaskProps {
  task: TaskType;
  onDrag: (id: string, status: string) => void;
  index: number; // Add index for the position in the list
}

const Task: React.FC<TaskProps> = ({ task, onDrag, index }) => {
  const getColor = (status: string) => {
    switch (status) {
      case 'TODO':
        return 'yellow';
      case 'DOING':
        return 'blue';
      case 'DONE':
        return 'green';
      default:
        return 'white';
    }
  };

  return (
    <Draggable
      axis="y"
      onStop={(e, data) => onDrag(task.id, task.status)} // Change status on drop
      position={{ x: 0, y: index * 60 }} // Adjust for dragging
    >
      <div style={{
        backgroundColor: getColor(task.status),
        border: '1px solid black',
        padding: '10px',
        margin: '5px',
        cursor: 'grab'
      }}>
        <h3>{task.title}</h3>
        <p>{task.description}</p>
      </div>
    </Draggable>
  );
};

export default Task;
