// src/components/KanbanBoard.tsx

import React, { useState } from 'react';
import Task from './Task';
import { Task as TaskType } from '../types';

const KanbanBoard: React.FC = () => {
  const [tasks, setTasks] = useState<TaskType[]>([]);
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const addTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!title || !description) return;
    const newTask: TaskType = {
      id: Date.now().toString(),
      title,
      description,
      status: 'TODO',
    };
    setTasks([...tasks, newTask]);
    setTitle('');
    setDescription('');
  };

  const onDrag = (id: string, currentStatus: string) => {
    const newStatus = currentStatus === 'TODO' ? 'DOING' : currentStatus === 'DOING' ? 'DONE' : 'TODO';

    setTasks(tasks.map(task => 
      task.id === id ? { ...task, status: newStatus } : task
    ));
  };

  return (
    <div>
      <h1>Kanban TODO App</h1>
      <form onSubmit={addTask}>
        <input
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
        />
        <textarea
          placeholder="Description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
        />
        <button type="submit">Add Task</button>
      </form>

      <div style={{ display: 'flex', justifyContent: 'space-between' }}>
        {['TODO', 'DOING', 'DONE'].map((status) => (
          <div key={status} style={{
            width: '30%',
            padding: '10px',
            border: '1px solid black',
            backgroundColor: status === 'TODO' ? 'lightyellow' : status === 'DOING' ? 'lightblue' : 'lightgreen',
          }}>
            <h2>{status}</h2>
            {tasks
              .filter(task => task.status === status)
              .map((task, index) => (
                <Task key={task.id} task={task} onDrag={onDrag} index={index} />
              ))}
          </div>
        ))}
      </div>
    </div>
  );
};

export default KanbanBoard;
