// src/components/TaskForm.tsx

import React, { useState } from 'react';
import { Task as TaskType } from '../types';

interface TaskFormProps {
  addTask: (task: TaskType) => void; // Update to accept a Task object
  status: 'TODO' | 'DOING' | 'DONE'; // Accept the status as a prop
}

const TaskForm: React.FC<TaskFormProps> = ({ addTask, status }) => {
  const [title, setTitle] = useState('');
  const [description, setDescription] = useState('');

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    const newTask: TaskType = {
      id: Math.random().toString(36).substr(2, 9), // Generate a random id
      title,
      description,
      status,
    };
    addTask(newTask); // Pass the entire task object
    setTitle('');
    setDescription('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Task Title"
        value={title}
        onChange={(e) => setTitle(e.target.value)}
        required
      />
      <textarea
        placeholder="Task Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
        required
      />
      <button type="submit">Add Task</button>
    </form>
  );
};

export default TaskForm;
