// src/components/KanbanBoard.tsx

import React, { useState } from 'react';
import Task from '../Task/Task';
import TaskForm from '../TaskForm/TaskForm';
import { DragDropContext, Droppable } from 'react-beautiful-dnd';
import Modal from 'react-modal';
import { Task as TaskType } from '../types';
import { Button, TextField } from '@mui/material';

Modal.setAppElement('#root'); // Set the app element for accessibility


const customStyles = {
    content: {
      top: "50%",
      left: "50%",
      right: "auto",
      bottom: "auto",
      marginRight: "-50%",
      transform: "translate(-50%, -50%)",
    },
  };


const KanbanBoard: React.FC = () => {
  const [tasks, setTasks] = useState<TaskType[]>([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [currentTask, setCurrentTask] = useState<TaskType | null>(null);

  const addTask = (newTask: TaskType) => {
    setTasks([...tasks, newTask]);
  };

  const openModal = (task: TaskType) => {
    setCurrentTask(task);
    setCurrentTaskId(task.id);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTaskId(null);
    setCurrentTask(null);
  };

  const handleEditTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentTask) return;

    const updatedTasks = tasks.map(task =>
      task.id === currentTaskId ? { ...task, title: currentTask.title, description: currentTask.description } : task
    );
    setTasks(updatedTasks);
    closeModal();
  };

  const onDragEnd = (result: any) => {
    if (!result.destination) return;

    const updatedTasks = Array.from(tasks);
    const [movedTask] = updatedTasks.splice(result.source.index, 1);

    // Update the status of the moved task based on the droppableId
    movedTask.status = result.destination.droppableId as 'TODO' | 'DOING' | 'DONE'; // Ensure the type is correct
    updatedTasks.splice(result.destination.index, 0, movedTask);

    setTasks(updatedTasks);
  };

   // Function to delete a task by its ID
   const handleDeleteTask = (taskId: string) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };


  return (
    <div>
      {/* <h1>Kanban TODO App</h1> */}
      <TaskForm addTask={addTask} />

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: 'flex', justifyContent: 'space-between', width: '95%', height:"100%"}}>
          {['TODO', 'DOING', 'DONE'].map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    width: '30%',
                    padding: '10px',
                    height: "100%",
                    // border: '1px solid black',
                    backgroundColor: status === 'TODO' ? 'lightyellow' : status === 'DOING' ? 'lightblue' : 'lightgreen',
                  }}
                >
                  <h2>{status}</h2>
                  {tasks
                    .filter(task => task.status === status)
                    .map((task, index) => (
                      <Task key={task.id} task={task} index={index} onClick={() => openModal(task)}  onDelete={() => handleDeleteTask(task.id)} />
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>

      {/* Modal for editing tasks */}
      {/* <Modal
        isOpen={modalIsOpen}
        onRequestClose={closeModal}
        contentLabel="Edit Task"
        style={{
          content: {
            top: '50%',
            left: '50%',
            right: 'auto',
            bottom: 'auto',
            transform: 'translate(-50%, -50%)',
          },
        }}
      >
        <h2>Edit Task</h2>
        {currentTask && (
          <form onSubmit={handleEditTask}>
            <input
              type="text"
              placeholder="Title"
              value={currentTask.title}
              onChange={(e) => setCurrentTask({ ...currentTask, title: e.target.value })}
              required
            />
            <textarea
              placeholder="Description"
              value={currentTask.description}
              onChange={(e) => setCurrentTask({ ...currentTask, description: e.target.value })}
              required
            />
            <button type="submit">Save Changes</button>
            <button type="button" onClick={closeModal}>Cancel</button>
          </form>
        )}
      </Modal> */}




      <Modal
       isOpen={modalIsOpen}
       onRequestClose={closeModal}
       contentLabel="Edit Task"
        style={customStyles}
      >
        <h2>Edit Card</h2>
        {currentTask && (
        <div>
          <form onSubmit={handleEditTask}>
            <TextField
              fullWidth
              id="fullWidth"
              placeholder="Edit Title"
              value={currentTask.title}
              onChange={(e) => setCurrentTask({ ...currentTask, title: e.target.value })}
              required
              sx={{ marginBottom: "10px" }}
            />
            <TextField
              fullWidth
              id="fullWidth"
              value={currentTask.description}
              onChange={(e) => setCurrentTask({ ...currentTask, description: e.target.value })}
              required
              placeholder="Edit Description"
              sx={{ marginBottom: "10px" }}
            />
            <div style={{ justifyContent: "space-between", display: "flex" }}>
              <Button type="button" variant="contained" onClick={closeModal}>
                Close
              </Button>
              <Button type="submit" variant="contained">
                Submit
              </Button>
            </div>
          </form>
        </div>
         )}
      </Modal> 
    </div>
  );
};

export default KanbanBoard;




