// src/components/KanbanBoard.tsx

import React, { useState } from 'react';
import Task from '../Task/Task';
import TaskForm from '../TaskForm/TaskForm';
import { DragDropContext, Droppable, Draggable, DropResult } from 'react-beautiful-dnd';
import Modal from 'react-modal';
import { Task as TaskType } from '../types';
import { Button, TextField } from '@mui/material';

Modal.setAppElement('#root');

const customStyles = {
  content: {
    top: "50%",
    left: "50%",
    right: "auto",
    bottom: "auto",
    marginRight: "-50%",
    transform: "translate(-50%, -50%)",
  },
};

const KanbanBoard: React.FC = () => {
  const [tasks, setTasks] = useState<TaskType[]>([]);
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [currentTaskId, setCurrentTaskId] = useState<string | null>(null);
  const [currentTask, setCurrentTask] = useState<TaskType | null>(null);

  const addTask = (newTask: TaskType) => {
    setTasks([...tasks, newTask]);
  };

  const openModal = (task: TaskType) => {
    setCurrentTask(task);
    setCurrentTaskId(task.id);
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
    setCurrentTaskId(null);
    setCurrentTask(null);
  };

  const handleEditTask = (e: React.FormEvent) => {
    e.preventDefault();
    if (!currentTask) return;

    const updatedTasks = tasks.map(task =>
      task.id === currentTaskId ? { ...task, title: currentTask.title, description: currentTask.description } : task
    );
    setTasks(updatedTasks);
    closeModal();
  };

  const onDragEnd = (result: DropResult) => {
    const { source, destination } = result;
    if (!destination) return; // Exit if dropped outside

    // Copy of tasks to work with
    const updatedTasks = Array.from(tasks);

    // Remove task from the source index
    const [movedTask] = updatedTasks.splice(source.index, 1);

    // Update the task's status to the new droppable destination ID
    movedTask.status = destination.droppableId as 'TODO' | 'DOING' | 'DONE';

    // Insert task at the new destination index
    updatedTasks.splice(destination.index, 0, movedTask);

    // Update the state with reordered tasks
    setTasks(updatedTasks);
  };

  const handleDeleteTask = (taskId: string) => {
    setTasks(tasks.filter(task => task.id !== taskId));
  };

  return (
    <div style={{ height: '100vh', overflow: 'hidden' }}>
      <TaskForm addTask={addTask} />

      <DragDropContext onDragEnd={onDragEnd}>
        <div style={{ display: 'flex', justifyContent: 'space-between', width: '100%', height: '100%' }}>
          {['TODO', 'DOING', 'DONE'].map((status) => (
            <Droppable key={status} droppableId={status}>
              {(provided) => (
                <div
                  ref={provided.innerRef}
                  {...provided.droppableProps}
                  style={{
                    width: '30%',
                    padding: '10px',
                    height: '100%',
                    overflowY: 'auto', // Allow scrolling within each column
                    backgroundColor: status === 'TODO' ? 'lightyellow' : status === 'DOING' ? 'lightblue' : 'lightgreen',
                    borderRadius: '5px',
                  }}
                >
                  <h2>{status}</h2>
                  {tasks
                    .filter(task => task.status === status)
                    .map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            style={{
                              ...provided.draggableProps.style,
                              margin: '5px 0',
                            }}
                          >
                            <Task
                              task={task}
                              index={index}
                              onClick={() => openModal(task)}
                              onDelete={() => handleDeleteTask(task.id)}
                            />
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>

      <Modal isOpen={modalIsOpen} onRequestClose={closeModal} contentLabel="Edit Task" style={customStyles}>
        <h2>Edit Card</h2>
        {currentTask && (
          <form onSubmit={handleEditTask}>
            <TextField
              fullWidth
              placeholder="Edit Title"
              value={currentTask.title}
              onChange={(e) => setCurrentTask({ ...currentTask, title: e.target.value })}
              required
              sx={{ marginBottom: '10px' }}
            />
            <TextField
              fullWidth
              placeholder="Edit Description"
              value={currentTask.description}
              onChange={(e) => setCurrentTask({ ...currentTask, description: e.target.value })}
              required
              sx={{ marginBottom: '10px' }}
            />
            <div style={{ display: 'flex', justifyContent: 'space-between' }}>
              <Button type="button" variant="contained" onClick={closeModal}>
                Close
              </Button>
              <Button type="submit" variant="contained">
                Submit
              </Button>
            </div>
          </form>
        )}
      </Modal>
    </div>
  );
};

export default KanbanBoard;









